import React, { Fragment } from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "./home.css";
import "./nav.css";
import { useParams } from "react-router-dom";

export default function Chome() {
    const { date, company } = useParams();
    const [single_s, setsingle_s] = useState([]);
    const [merge_s, setmerge_s] = useState([]);
    const handlemerge = async (event) => {
        event.preventDefault();

        //to mergepage with date and sid1 and sid2
        window.location.href = "/merge/"+event.target.value[0]+"/"+event.target.value[1];
    };




    useEffect(() => {
        axios.get('http://localhost:5000/chome/' + date + '/' + company, { withCredentials: true })
            .then(res => {
                setsingle_s(res.data.single_s);
                setmerge_s(res.data.merge_s);

            })
            .catch(err => console.log(err));
    }, []);



    return (
        <Fragment>
            <table>
                <thead>
                    <tr>
                        <th>sid</th>
                        <th>from_loc</th>
                        <th>to_loc</th>
                        <th>from_time</th>
                        <th>to_time</th>
                        <th>price</th>
                        <th>available</th>
                        <th>remove</th>
                    </tr>
                </thead>
                <tbody>
                    {single_s.map((item) => (
                        <tr key={item.sid}>
                            <td>{item.sid}</td>
                            <td>{item.from_loc}</td>
                            <td>{item.to_loc}</td>
                            <td>{item.from_time}</td>
                            <td>{item.to_time}</td>
                            <td>{item.price}</td>
                            <td>{item.capacity - item.filled}</td>
                            <td><button>remove</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>



            <table>
                <thead>
                    <tr>
                        <th>sid1</th>
                        <th>sid2</th>
                        <th>from_loc</th>
                        <th>from_time</th>
                        <th>mid_loc</th>
                        <th>mid_time</th>
                        <th>to_loc</th>
                        <th>to_time</th>
                        <th>merge
                            </th>


                    </tr>
                </thead>
                <tbody>
                    {merge_s.map((item) => (

                        <tr>
                            <td>{item.sid1}</td>
                            <td>{item.sid2}</td>
                            <td>{item.from_loc}</td>
                            <td>{item.from_time}</td>
                            <td>{item.mid_loc}</td>
                            <td>{item.mid_time}</td>
                            <td>{item.to_loc}</td>
                            <td>{item.to_time}</td>
                            //button merge on click handlemerge

                            <td><button type = 'submit' onClick = {() => handlemerge(item.sid1, item.sid2)}>merge</button></td>

                        </tr>
                    ))}
                </tbody>





            </table>
















        </Fragment>
    );
}
